import React, { useState } from 'react';
import './App.css';

const Address4 = require('ip-address').Address4;

const App = () => {
  const [input, setInput] = useState("");

  const handleChange = (val) => {
    // console.log(val);
    setInput(val);
  }

  const handleClick = (e) => {
    // console.log(input);
    const address = new Address4(input);
    console.log(address.toHex());
    // setInput("");
    e.preventDefault();
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>IP Locator</h1>
      </header>
      <main>
        <form>
          <label htmlFor="ip">Enter an IP Address:</label>
          <input type="text" id="ip" name="ip" placeholder="0.0.0.0" value={input} onChange={e => handleChange(e.target.value)} />
          <input type="submit" value="Submit" onClick={e => handleClick(e)} />
        </form>
        <div>
          <p>Latitude:</p>
          <p>Longitude:</p>
        </div>
      </main>
    </div>
  );
}

export default App;
